import Vue from 'vue'
import Vuex from 'vuex'
import defaultData from '@/data'
import http from "@/http/http.js";
import EventBus from '@/eventBus';
import cryptojs from 'vue-cryptojs';
import sha256 from 'sha256';
import {
    storage,
    Storage
} from "@/storage";

Vue.use(Vuex)

export default new Vuex.Store({
    state: {
        data: defaultData,
        user: {
            init: true,
            username: "admin",
            password: "password"
        },
        climatikUser: null,
        code_verifier: '',
        users: {},
        roles: null,
        aliments: null,
        applications: [],
        policies: [],
        base64URLEncode: str => str.toString('base64')
            .replace(/\+/g, '-')
            .replace(/\//g, '_')
            .replace(/=/g, ''),
        strRandom: o => {
            var a = 10,
                b = 'abcdefghijklmnopqrstuvwxyz',
                c = '',
                d = 0,
                e = '' + b;
            if (o) {
                if (o.startsWithLowerCase) {
                    c = b[Math.floor(Math.random() * b.length)];
                    d = 1;
                }
                if (o.length) {
                    a = o.length;
                }
                if (o.includeUpperCase) {
                    e += b.toUpperCase();
                }
                if (o.includeNumbers) {
                    e += '1234567890';
                }
            }
            for (; d < a; d++) {
                c += e[Math.floor(Math.random() * e.length)];
            }
            return c;
        },
    },
    mutations: {
        removeUser(state) {
            this.state.user = null;
        },
        setUser(state, data) {
            this.state.user = data;
            this.dispatch('loadUsers');
            this.dispatch('loadAliments');
            this.dispatch('loadPolicies');
            this.dispatch('loadRoles');
            EventBus.$emit('user:connected', data)
        },
        setUsers(state, data) {
            this.state.users = data.map(
                function(user) {
                    user.hasRoles = {};
                    user = this
                        .reduce((acc, role) => {
                            acc.hasRoles[role.nom] = acc.authorities.find(authority => role.nom == authority.authorities, {}) != null;
                            return acc
                        }, user);
                    return user;
                }, this.state.roles);
            EventBus.$emit('users:loaded', data)
        },
        setClimatikUser(state, data) {
            this.state.climatikUser = data
            EventBus.$emit('climatikUser:loaded', data)
        },
        logOut(state, payload) {
            if (payload.ok) {
                EventBus.$emit('user:disconnected', payload)
            } else {
                EventBus.$emit('user:disconnected:error', payload)
            }
            this.state.user = {
                init: true,
            };
            this.state.data = defaultData;
        },
        setAliments(state, payload) {
            this.state.aliments = payload;
            EventBus.$emit('aliments:loaded', payload)
        },
        setRoles(state, data) {
            EventBus.$emit('roles:loaded', data);
            this.state.roles = data;
        },
        setPolicies(state, data) {
            this.state.policies = data;
            EventBus.$emit('policies:loaded', data)
        },
    },
    actions: {
        loadUser({
            commit
        }, user) {
            http
                .login(user)
                .then(response => {
                    response.json().then(function(data) {
                            commit('setUser', data)
                        })
                        .catch(error => {
                            console.log(error);
                            EventBus.$emit('user:disconnected')
                        });
                    EventBus.$emit('user:disconnected')
                })
                .catch(error => {
                    console.log(error);
                    EventBus.$emit('user:disconnected')
                });
        },
        loadUsers({
            commit
        }) {
            http
                .loadUsers()
                .then(response => response.json())
                .then(response => {
                    if (response.ok) {
                        response.json().then(function(data) {
                            commit('setUsers', data)
                        })
                    } else {
                        commit('setUsers', response)
                    }
                })
                .catch(error => {
                    console.log(error);
                    EventBus.$emit('users:cantload')
                });
        },
        loadClimatikUser({
            commit
        }) {
            http
                .loginClimatikUser()
                .then(response => {
                    if (response.ok) {
                        response.json().then(function(data) {
                            commit('setClimatikUser', data)
                        })
                    } else {
                        commit('setClimatikUser', response)
                    }
                })
                .catch(error => {
                    console.log(error);
                    EventBus.$emit('climatikUrser:disconnected')
                });
        },
        route({
            commit
        }, code) {
            http
                .route(code)
                .then(response => {
                    if (response.ok) {
                        response.json().then(function(data) {
                            commit('setClimatikUser', data)
                        })
                    } else {
                        commit('setClimatikUser', response)
                    }
                })
                .catch(error => {
                    console.log(error);
                    EventBus.$emit('climatikUrser:disconnected')
                });
        },
        logOut({
            commit
        }) {
            http
                .logOut()
                .then(response => {
                    commit('logOut', response);
                })
                .catch(error => {
                    console.log(error)
                    EventBus.$emit('users:cantlogOut')
                });
        },
        loadAliments({
            commit
        }) {
            http
                .loadAliments()
                .then(response => {
                    if (response.ok) {
                        response.json().then(function(data) {
                            commit('setAliments', data);
                        })
                    } else {
                        commit('setAliments', response)
                    }
                })
                .catch(error => {
                    console.log(error)
                    EventBus.$emit('aliment:cantget')
                });
        },
        loadRoles({
            commit
        }) {
            http
                .loadRoles()
                .then(response => {
                    if (response.ok) {
                        response.json().then(function(data) {
                            commit('setRoles', data);
                        })
                    } else {
                        commit('setRoles', response)
                    }
                })
                .catch(error => {
                    console.log(error)
                    EventBus.$emit('roles:cantget')
                });
        },
        addRole({
            commit
        }, role) {
            console.log('evenement add rôle bien reçu' + role)
            http
                .addRoles(role)
                .then(response => {
                    if (response.ok) {
                        commit('roles:added', response.headers.get('Location'))
                        this.dispatch('loadRoles');
                    } else {
                        commit('roles/added:fail', response.json())
                    }
                })
                .catch(error => {
                    console.log(error)
                    EventBus.$emit('roles:cantget')
                });
        },
        loadPolicies({
            commit
        }) {
            http
                .loadPolicies()
                .then(response => {
                    if (response.ok) {
                        response.json().then(function(data) {
                            commit('setPolicies', data);
                        })
                    } else {
                        commit('setPolicies', response)
                    }
                })
                .catch(error => {
                    console.log(error)
                    EventBus.$emit('policies:cantget')
                });
        },
    },
    getters: {
        isLogged(state) {
            return state.user != null;
        },
        codeChallenge(state) {
            let challenge = '';
            let verifier = localStorage.getItem("climatik_verifier") == null ||  localStorage.getItem("climatik_verifier").length < 10 ?
                state.base64URLEncode(state.strRandom({
                    startsWithLowerCase: true,
                    length: 32,
                    includeUpperCase: true,
                    includeNumbers: true,
                })) :
                localStorage.getItem("climatik_verifier");
            localStorage.setItem("climatik_verifier", verifier);
            challenge = state.base64URLEncode(sha256(verifier));
            state.code_challenge = challenge;
            return challenge;
        },
    }
})
